import json
from django.test import TestCase
from unittest.mock import patch
from .models import Player
from django.http import JsonResponse

class PlayerCreationTestCase(TestCase):
    
    @patch('django.core.files.base.ContentFile')  # Mock ContentFile for image saving
    def test_create_player_with_image(self, mock_content_file):
        print(Player.objects.count())
        player_data = {
            'id': 10221,
            'name': 'Test Player',
            'status': 0,
            'tags': 1,
            'image': 'data:image/png;base64,'
        }

        # Directly specify the URL
        response = self.client.post(
            '/api/player_creation/',  # Direct URL path
            data=json.dumps(player_data),
            content_type='application/json'
        )
        
        print(Player.objects.count())
        
        
        
        self.assertEqual(response.status_code, 201)
        self.assertEqual(Player.objects.count(), 1)

